INSERT INTO `exercises` VALUES
(1,'Muuda lehe taustavärv punaseks','Muuda kõrvalasuva koodiredaktoriga lehe taustavärv punaseks.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 1</title>\n    <style>body {background-color: blue}</style>\n</head>\n<body>\n    <h1>Hello world!</h1>\n</body>\n</html>','function validate() {\n return window.getComputedStyle(document.body).backgroundColor === \'rgb(255, 0, 0)\';\n}'),
(2,'Lisa lehele nupp','Lisa kõrvalasuva koodiredaktoriga lehele üks nupp kasutades <code>button</code> elementi.','<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Ülesanne 2</title>\n</head>\n<body>\n    <h1>Button Check</h1>\n    <p>Some text</p>\n</body>\n</html>','function validate() {\n return document.getElementsByTagName(\'button\').length > 0;\n}\n'),
(3,'Muuda lehe fonti','Muuda kõrvalasuva koodiredaktoriga lehe font Arialiks.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 3</title>\n    <style>body {font-family: sans-serif;}</style>\n</head>\n<body>\n    <h1>Tere maailm!</h1>\n    <p>See on lihtne HTML leht.</p>\n</body>\n</html>','function validate() {\n return window.getComputedStyle(document.body).fontFamily.toLowerCase().includes(\'arial\'.toLowerCase());\n}'),
(4,'Muuda pildi laiust','Muuda pildi laiuseks 200 pikslit.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 4</title>\n</head>\n<body>\n    <img src=\"https://via.placeholder.com/150\" alt=\"Näidispilt\">\n</body>\n</html>','function validate() {\n return document.querySelector(\'img\').width === 200;\n}'),
(5,'Märgista tekstkast kohustuslikuks','Märgista lisatud tekstkast kohustuslikuks.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 5</title>\n</head>\n<body>\n    <input type=\"text\">\n</body>\n</html>','function validate() {\n return document.querySelector(\'input\').required;\n}'),
(6,'Lisa lehele form e-maili väliga','Lisa lehele vorm ja sisesta sinna e-maili väli.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 6</title>\n</head>\n<body>\n    <!-- Lisa vorm siia -->\n</body>\n</html>','function validate() {\n return !!document.querySelector(\'form\') && !!document.querySelector(\'input[type=\"email\"]\');\n}'),
(7,'Muuda tekste positsiooni','Muuda lehe tekst keskel joondatuks (kasuta <code>text-align: center</code>).','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 7</title>\n    <style>body {text-align: left;}</style>\n</head>\n<body>\n    <h1>Tere maailm!</h1>\n    <p>See on lihtne HTML leht.</p>\n</body>\n</html>','function validate() {\n return window.getComputedStyle(document.body).textAlign === \'center\';\n}\n'),
(8,'Lisa pilt lehele','Lisa lehele pilt <a href=\"https://bit.ly/ipz\">https://bit.ly/ipz</a>.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 8</title>\n</head>\n<body>\n    <!-- Lisa pilt siia -->\n</body>\n</html>','function validate() {\n return document.querySelector(\'img\') !== null;\n}\n'),
(9,'Lisa märkeruut ja märgista valituks','Lisa vormile märkeruut (linnuke) ja märgista see vaikimisi valituks.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 9</title>\n</head>\n<body>\n    <!-- Lisa märkeruut siia -->\n</body>\n</html>','function validate() {\n return document.querySelector(\'input[type=\"checkbox\"]\').checked;\n}\n'),
(10,'Lisa div \'konteiner\' klassiga','Lisa lehele <code>div</code> element ja anna sellele klass \"<code>konteiner</code>\".','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 21</title>\n</head>\n<body>\n    <!-- Lisa div siia -->\n</body>\n</html>','function validate() {\n return document.querySelector(\'div.konteiner\') !== null;\n}\n'),
(11,'Lisa raadio nuppud ','Lisa lehele raadionupud väärtustega \"yes\" ja \"no\".','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 11</title>\n</head>\n<body>\n    <!-- Lisa raadio nupud siia -->\n</body>\n</html>','function validate() {\n const radios = document.querySelectorAll(\'input[type=\"radio\"]\');\n if (radios.length !== 2) {\n return false;\n }\n const values = Array.from(radios).map(radio => radio.value);\n return values.includes(\"yes\") && values.includes(\"no\");\n}'),
(12,'Lisa rippmenüü lehele','Lisa vormile rippmenüü väärtustega \"day\", \"month\" ja \"year\".\n','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 12</title>\n</head>\n<body>\n    <!-- Lisa rippmenüü siia -->\n</body>\n</html>','function validate() {\n const options = document.querySelectorAll(\'option\');\n if (options.length !== 3) {\n return false;\n }\n const values = Array.from(options).map(option => option.value);\n return values.includes(\"day\") && values.includes(\"month\") && values.includes(\"year\");\n}'),
(13,'Piira sisendi pikkus','Lisa lehele tekstikast ja piira sisendi pikkus maksimaalselt 10 tähemärgini.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 13</title>\n</head>\n<body>\n    <input type=\"text\">\n</body>\n</html>','function validate() {\n return document.querySelector(\'input\').maxLength === 10;\n}'),
(14,'Lisa tabel lehele','Lisa lehele tabel, kus on 2 rida ja 2 veergu.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 14</title>\n</head>\n<body>\n    <!-- Lisa tabel siia -->\n</body>\n</html>','function validate() {\n const rows = document.querySelectorAll(\'table tr\');\n const cells = document.querySelectorAll(\'table td\');\n return rows.length === 2 && cells.length === 4;\n}\n'),
(15,'Lisa korrektne label sisestusväli jaoks','Lisa korrektne sildi-element (<code>label</code>), mis on seotud nime sisestusväljaga. Kasuta selleks <code>for</code> atribuuti.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 15</title>\n</head>\n<body>\n    <!-- Lisa label siia -->\n    \n    <input type=\"text\" id=\"name\" name=\"name\">\n</body>\n</html>','function validate() {\n const label = document.querySelector(\'label\');\n const input = document.querySelector(\'input[type=\"text\"]\');\n return label && input && label.getAttribute(\'for\') === input.id;\n}\n'),
(16,'Lisa nummerdamata loend lehele','Loo HTML-is nummerdamata loend (<code>ul</code> märgend), mis sisaldab kolme eset: \"Punane\", \"Roheline\", \"Sinine\".','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: 16</title>\n</head>\n<body>\n    <!-- Lisa loend siia -->\n</body>\n</html>','function validate() {\n const listItems = document.querySelectorAll(\'ul li\');\n const expectedItems = [\'Punane\', \'Roheline\', \'Sinine\'];\n const itemTexts = Array.from(listItems).map(item => item.textContent.trim());\n return listItems.length === 3 && expectedItems.every(item => itemTexts.includes(item));\n}\n'),
(17,'Muuda lingi tekst suureks täheks','Muuda lingi tekst \"Kliki siin\" suurteks tähtedeks kasutades text-transformi.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 17</title>\n    <style>a {text-transform: none;}</style>\n</head>\n<body>\n    <a href=\"#\">Kliki siin</a>\n</body>\n</html>','function validate() {\n return window.getComputedStyle(document.querySelector(\'a\')).textTransform === \'uppercase\';\n}\n'),
(18,'Muuda tekstiala laius','Muuda tekstiala laius 300 piksliks.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne 18</title>\n    <style>\n       /* Lisa CSS siin */\n       \n    </style>\n</head>\n<body>\n    <textarea></textarea>\n</body>\n</html>','function validate() {\n const computedWidth = window.getComputedStyle(document.querySelector(\'textarea\')).width;\n return computedWidth === \'300px\';\n}\n'),
(19,'Muuda fondi värv \"highlight\" klassi jaoks','Muuda lehel olevate elementide, millel on klass \"highlight\", fondi värv punaseks.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: 19</title>\n    <style>\n        /* Lisa CSS siin */\n    </style>\n</head>\n<body>\n    <p class=\"highlight\">See tekst peab olema punane.</p>\n    <p>See tekst jääb mustaks.</p>\n    <span class=\"highlight\">Ka see tekst peab olema punane.</span>\n    <div class=\"highlight\">Punane tekst div sees.</div>\n</body>\n</html>','function validate() {\n const highlightedElements = document.querySelectorAll(\'.highlight\');\n return Array.from(highlightedElements).every(el => window.getComputedStyle(el).color === \'rgb(255, 0, 0)\');\n}'),
(20,'Muuda kastile (div element) ümarad nurgad','Lisa lehel olevale kastile (<code>div</code> element) ümarad nurgad, kasutades CSS-i omadust <code>border-radius</code>. Määra <code>border-radius</code> väärtuseks 15 pikslit.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: 20</title>\n    <style>\n        /* Lisa CSS siin */\n        div.box {\n            width: 100px;\n            height: 100px;\n            background-color: lightblue;\n        }\n    </style>\n</head>\n<body>\n    <div class=\"box\"></div>\n</body>\n</html>','function validate() {\n const box = document.querySelector(\'.box\');\n const borderRadius = window.getComputedStyle(box).borderRadius;\n return borderRadius === \'15px\';\n}'),
(21,'Muuda kastile (div element) marginaal','Lisa lehel olevale kastile (<code>div</code>) marginaal (<code>margin</code>), kasutades CSS-i omadust. Määra marginaali väärtuseks 15 pikslit ülemisele küljele, 5 pikslit paremale küljele, 6 pikslit alumisele küljele ja 7 pikslit vasakule küljele.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: 21</title>\n    <style>\n        /* Lisa CSS siin */\n        div.box {\n            width: 100px;\n            height: 100px;\n            background-color: lightblue;\n            /* Lisa marginaal siia */\n        }\n    </style>\n</head>\n<body>\n    <div class=\"box\"></div>\n</body>\n</html>','function validate() {\n const box = document.querySelector(\'.box\');\n const computedStyle = window.getComputedStyle(box);\n \n const topMargin = computedStyle.marginTop === \'15px\';\n const rightMargin = computedStyle.marginRight === \'5px\';\n const bottomMargin = computedStyle.marginBottom === \'6px\';\n const leftMargin = computedStyle.marginLeft === \'7px\';\n \n return topMargin && rightMargin && bottomMargin && leftMargin;\n}\n'),
(22,'Muuda kastile (div element) sisemine polsterdus','Lisa lehel olevale kastile (<code>div</code>) sisemine polsterdus (<code>padding</code>), kasutades CSS-i omadust. Määra polsterduse väärtuseks 12 pikslit ülemisele küljele, 8 pikslit paremale küljele, 10 pikslit alumisele küljele ja 14 pikslit vasakule küljele.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: Padding Lisamine</title>\n    <style>\n        /* Lisa CSS siin */\n        div.box {\n            width: 100px;\n            height: 100px;\n            background-color: lightcoral;\n            /* Lisa padding siia */\n        }\n    </style>\n</head>\n<body>\n    <div class=\"box\">Sisu</div>\n</body>\n</html>','function validate() {\n const box = document.querySelector(\'.box\');\n const computedStyle = window.getComputedStyle(box);\n \n const topPadding = computedStyle.paddingTop === \'12px\';\n const rightPadding = computedStyle.paddingRight === \'8px\';\n const bottomPadding = computedStyle.paddingBottom === \'10px\';\n const leftPadding = computedStyle.paddingLeft === \'14px\';\n \n return topPadding && rightPadding && bottomPadding && leftPadding;\n}\n'),
(23,'Muuda elementide display omadust ','Muuda lehel olevaid elemente kasutades CSS-i omadust <code>display</code><br>\nPeida lõik (<code>p</code> element) lehelt.<br>\nMuuda nupp (<code>button</code> element) plokk-tasandiliseks(<code>block</code>).','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: 23</title>\n    <style>\n        /* Lisa CSS siin */\n    </style>\n</head>\n<body>\n    <p>See on lõik, mida tuleb peita.</p>\n    <button>Klikka mind</button>\n</body>\n</html>','function validate() {\n const paragraph = window.getComputedStyle(document.querySelector(\'p\')).display;\n const button = window.getComputedStyle(document.querySelector(\'button\')).display;\n \n const isParagraphHidden = paragraph === \'none\';\n const isButtonBlock = button === \'block\';\n \n return isParagraphHidden && isButtonBlock;\n}\n'),
(24,'Kasuta erinevaid CSS-i ühikuid','Muuda lehel olevate elementide suurust ja asukohta kasutades erinevaid CSS-i ühikuid:<br>\nMäära pealkirja (<code>h1</code> element) font suuruseks 2em.<br>\nMäära kasti (<code>div</code> element) laius 50% ja kõrgus 100 pikslit (px).\n','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: 24</title>\n    <style>\n        /* Lisa CSS siin */\n        body {\n            margin: 0;\n            padding: 20px;\n        }\n        div {\n            background-color: lightcoral;\n        }\n    </style>\n</head>\n<body>\n    <h1>Tere tulemast!</h1>\n    <div>Sisu kastis</div>\n</body>\n</html>','function validate() {\n const headingFontSize = window.getComputedStyle(document.querySelector(\'h1\')).fontSize;\n const divWidth = window.getComputedStyle(document.querySelector(\'div\')).width;\n const divHeight = window.getComputedStyle(document.querySelector(\'div\')).height;\n\n // Assuming the body\'s width is 1000px for this example, 50% of 1000px is 500px\n const isHeadingFontSizeCorrect = headingFontSize === \'32px\' || headingFontSize === \'2em\'; // 2em is generally 32px\n const isDivWidthCorrect = divWidth === \'50%\' || divWidth === \'500px\'; // 50% of container width, let\'s assume 500px here\n const isDivHeightCorrect = divHeight === \'100px\';\n\n return isHeadingFontSizeCorrect && isDivWidthCorrect && isDivHeightCorrect;\n}\n'),
(25,'Kasuta Flexboxi','Paiguta lehel olevad kastid (<code>div</code> elemendid) õigesti kasutades Flexbox omadusi:<br>\nSeadista konteiner (<code>.container</code>) kasutama Flexbox paigutust.<br>\nMuuda kastide paigutust nii, et nad oleksid üksteisest ühtlaselt eraldatud.<br>\nJoonda kastid vertikaalselt konteineri keskele.\n\n ','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: 25</title>\n    <style>\n        /* Lisa CSS siin */\n        .container {\n            width: 100%;\n            height: 150px;\n            background-color: #f0f0f0;\n        }\n        .box {\n            width: 100px;\n            height: 100px;\n            background-color: lightblue;\n            margin: 10px;\n        }\n    </style>\n</head>\n<body>\n    <div class=\"container\">\n        <div class=\"box\"></div>\n        <div class=\"box\"></div>\n        <div class=\"box\"></div>\n    </div>\n</body>\n</html>','function validate() {\n const container = document.querySelector(\'.container\');\n const containerDisplay = window.getComputedStyle(container).display;\n const justifyContent = window.getComputedStyle(container).justifyContent;\n const alignItems = window.getComputedStyle(container).alignItems;\n\n return containerDisplay === \'flex\' &&\n justifyContent === \'space-between\' &&\n alignItems === \'center\';\n}\n\n'),
(26,'Muuda kastide läbipaistvust','Muuda lehel olevate kastide (<code>div</code> elementide) läbipaistvust, kasutades CSS-i <code>opacity</code> omadust.<br>\nMäära esimese kasti (<code>.box1</code>) läbipaistvuseks 50%.<br>\nMäära teise kasti (<code>.box2</code>) läbipaistvuseks 20%.\n\n','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: 26</title>\n    <style>\n        /* Lisa CSS siin */\n        .box1, .box2 {\n            width: 100px;\n            height: 100px;\n            margin: 10px;\n            background-color: lightcoral;\n        }\n    </style>\n</head>\n<body>\n    <div class=\"box1\"></div>\n    <div class=\"box2\"></div>\n</body>\n</html>','function validate() {\n const box1Opacity = window.getComputedStyle(document.querySelector(\'.box1\')).opacity;\n const box2Opacity = window.getComputedStyle(document.querySelector(\'.box2\')).opacity;\n\n return box1Opacity === \'0.5\' && box2Opacity === \'0.2\';\n}\n'),
(27,'Lisa loendi numbritega','Loo HTML-i abil nummerdatud loend, mis sisaldab kolme eset: \"Esimene\", \"Teine\" ja \"Kolmas\".','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: 27</title>\n    <style></style>\n</head>\n<body>\n    <!-- Lisa nummerdatud loend siia -->\n</body>\n</html>','function validate() {\n const list = document.querySelector(\'ol\');\n const listItems = document.querySelectorAll(\'ol li\');\n\n const correctItems = listItems.length === 3 &&\n listItems[0].textContent.trim() === \'Esimene\' &&\n listItems[1].textContent.trim() === \'Teine\' &&\n listItems[2].textContent.trim() === \'Kolmas\';\n\n return list && correctItems;\n}\n'),
(28,'Kasuta HTML vorminduselementid','Kasutades HTML vorminduselemente, vorminda allolev lause järgmiselt:<br>\nMuuda sõna \"Oluline\" rasvaseks.<br>\nMuuda sõna \"tähtis\" kursiiviks.<br>\nLisa sõna \"veebileht\" alla joonitud tekstina.','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: 28</title>\n</head>\n<body>\n    <!-- Vorminda järgnev lause: -->\n    <p>See on Oluline sõnum, mis sisaldab tähtis teavet selle kohta, kuidas luua veebileht.</p>\n</body>\n</html>','function validate() {\r  const strongText = document.querySelector(\'strong, b\')?.textContent.trim() === \'Oluline\';\r  const emText     = document.querySelector(\'em, i\')?.textContent.trim() === \'tähtis\';\r  const uText      = document.querySelector(\'u\')?.textContent.trim() === \'veebileht\';\r\r  return strongText && emText && uText;\r}'),
(29,'Kasuta CSS järglase kombinaatori','Kasuta CSS-i järglase kombinaatorit, et stiliseerida kõik <code>span</code> elemendid, mis asuvad <code>div</code> elemendi sees.<br>\nMäära kõikide <code>div</code> elementide sees olevate <code>span</code> elementide tekstivärv siniseks.\n\n ','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: 29</title>\n    <style>\n        /* Lisa CSS siin */\n    </style>\n</head>\n<body>\n    <div>\n        <span>See tekst peab olema sinine.</span>\n    </div>\n    <p>\n        <span>See tekst peab jääma mustaks.</span>\n    </p>\n</body>\n</html>','function validate() {\n const divSpanColor = window.getComputedStyle(document.querySelector(\'div span\')).color;\n const pSpanColor = window.getComputedStyle(document.querySelector(\'p span\')).color;\n\n return divSpanColor === \'rgb(0, 0, 255)\' && pSpanColor !== \'rgb(0, 0, 255)\';\n}\n'),
(30,'Kasuta CSS kõrguse ja laiuse','Määra lehel olevale kastile <code>div</code> elemendile kindel laius ja kõrgus, kasutades CSS-i omadusi.<br>\nMäära kasti laiuseks 200 pikslit (px).<br>\nMäära kasti kõrguseks 150 pikslit (px).\n','<!DOCTYPE html>\n<html lang=\"et\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Ülesanne: 30</title>\n    <style>\n        /* Lisa CSS siin */\n        .box {\n            background-color: lightblue;\n        }\n    </style>\n</head>\n<body>\n    <div class=\"box\"></div>\n</body>\n</html>','function validate() {\n const box = document.querySelector(\'.box\');\n const boxWidth = window.getComputedStyle(box).width;\n const boxHeight = window.getComputedStyle(box).height;\n\n return boxWidth === \'200px\' && boxHeight === \'150px\';\n}\n');
